import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";
const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };
  // function for select year to filter expenses
  const filteredExpenses = props.item.filter((expense) => {
    return expense.date.getFullYear().toString();
  });
  return (
    <Card className="expenses">
      <ExpensesFilter
        selected={filteredYear}
        onChangeFilter={filterChangeHandler}
      />
      {/* filter expenses based on year */}
      {filteredExpenses.length === 0 ? (
        <p>No Expenses found!</p>
      ) : (
        // use array and map function to create dynamic ExpenseItem
        filteredExpenses.map((expense) => (
          // we should add such a key when mapping out list of item to render efficiently
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))
      )}

      {/* <ExpenseItem
        title={props.item[0].title}
        amount={props.item[0].amount}
        date={props.item[0].date}
      /> */}
    </Card>
  );
};
export default Expenses;
