import React from "react";
import "./App.css";
import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";
const App = () => {
  const expense = [
    {
      id: "e1",
      title: "Car Insurance",
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: "e2",
      title: "Toilet Paper",
      amount: 26.45,
      date: new Date(2021, 1, 8),
    },
    { id: "e3", title: "carpet", amount: 23.4, date: new Date(2021, 9, 5) },
    {
      id: "e4",
      title: "furniture",
      amount: 123.76,
      date: new Date(2020, 12, 20),
    },
  ];
  // call this function in child(NewExpense)
  const addExpenseHandler = (expenses) => {
    console.log("In App.js");
    console.log(expenses);
  };
  return (
    <div>
      {/* move data from child to parent by utilizing props to recieve function from parent which call in child componant  */}
      {/* onAddExpense is a function that send data from child(NewExpense) to parent(App) */}
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses item={expense} />
    </div>
  );
};

export default App;
